    private void GenerateButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_GenerateButtonActionPerformed
        current.setAdj1(Adj1Field.getText());
        current.setAdj2(Adj2Field.getText());
        current.setFam(FamField.getText());
        current.setFather(FatherField.getText());
        current.setImg(IMGField.getText());
        current.setItem(ItemField.getText());
        current.setJob(JobField.getText());
        current.setMother(MotherField.getText());
        current.setName(NameField.getText());
        current.setAdult(PreviewField.getText());
        current.setUrl(URLField.getText());
        if (Gen2Button.isSelected()) { current.setGen2(true);}
        
        CodeField.setText(current.makeBio());
    }//GEN-LAST:event_GenerateButtonActionPerformed

    private void RandomizeButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_RandomizeButtonActionPerformed
        current.genAll();
        
        Adj1Field.setText(current.getAdj1());
        Adj2Field.setText(current.getAdj2());
        JobField.setText(current.getJob());
        FamField.setText(current.getFam());
        ItemField.setText(current.getItem());
        NameField.setText(current.getName());
    }//GEN-LAST:event_RandomizeButtonActionPerformed

